css:
.section-cta-banner-simple-7q {
  background-color: var(--color-primary);
  padding: 30px 0;
  color: var(--color-light);
}
.section-cta-banner-simple-7q .container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
  gap: 20px;
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cta-banner-simple-7q .text-content {
  flex: 1;
}
.section-cta-banner-simple-7q .banner-text {
  font-size: 1.1rem;
  font-family: var(--font-body);
  margin: 0;
}
.section-cta-banner-simple-7q .button-wrapper {
  flex-shrink: 0;
}
.section-cta-banner-simple-7q .btn-cta {
  background-color: var(--color-light);
  color: var(--color-primary);
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;
  font-family: var(--font-body);
}
.section-cta-banner-simple-7q .btn-cta:hover {
  background-color: var(--color-primary-dark, #0056b3);
  color: var(--color-light);
}
@media (max-width: 768px) {
  .section-cta-banner-simple-7q .container {
    flex-direction: column;
    text-align: center;
  }
  .section-cta-banner-simple-7q .button-wrapper {
    width: 100%;
  }
  .section-cta-banner-simple-7q .btn-cta {
    width: 100%;
  }
}
