css:
.section-cta-icon-list-grouped-7p {
  padding: 80px 0;
  background-color: var(--color-surface-1);
  font-family: var(--font-body);
}
.section-cta-icon-list-grouped-7p .container {
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cta-icon-list-grouped-7p .section-header {
  text-align: center;
  margin-bottom: 50px;
}
.section-cta-icon-list-grouped-7p .section-title {
  font-size: clamp(2rem, 6vw, 3.5rem);
  font-family: var(--font-heading);
  font-weight: 700;
  color: var(--color-dark);
  margin-bottom: 15px;
}
.section-cta-icon-list-grouped-7p .section-subtitle {
  font-size: 1.1rem;
  color: var(--color-secondary);
  line-height: 1.6;
}
.section-cta-icon-list-grouped-7p .icon-groups {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 40px;
  margin-bottom: 50px;
}
.section-cta-icon-list-grouped-7p .icon-group {
  display: flex;
  align-items: flex-start;
  gap: 15px;
  text-align: left;
}
.section-cta-icon-list-grouped-7p .icon-wrapper {
  flex-shrink: 0;
  width: 40px;
  height: 40px;
  display: flex;
  justify-content: center;
  align-items: center;
}
.section-cta-icon-list-grouped-7p .icon-wrapper img {
  width: 100%;
  height: 100%;
  object-fit: contain;
  filter: var(--color-primary-filter, invert(45%) sepia(87%) saturate(1244%) hue-rotate(178deg) brightness(97%) contrast(103%));
}
.section-cta-icon-list-grouped-7p .icon-text {
  flex: 1;
}
.section-cta-icon-list-grouped-7p .icon-group-title {
  font-size: 1.3rem;
  font-family: var(--font-heading);
  font-weight: 700;
  color: var(--color-dark);
  margin-bottom: 10px;
}
.section-cta-icon-list-grouped-7p .icon-group-description {
  font-size: 0.95rem;
  color: var(--color-secondary);
  line-height: 1.6;
}
.section-cta-icon-list-grouped-7p .cta-button-container {
  text-align: center;
}
.section-cta-icon-list-grouped-7p .btn-cta-icon-list {
  background-color: var(--color-primary);
  color: var(--color-light);
  padding: 12px 30px;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.section-cta-icon-list-grouped-7p .btn-cta-icon-list:hover {
  background-color: var(--color-primary-dark, #0056b3);
}
@media (max-width: 768px) {
  .section-cta-icon-list-grouped-7p .icon-groups {
    grid-template-columns: 1fr;
  }
  .section-cta-icon-list-grouped-7p .icon-group {
    align-items: center;
  }
}
