css:
.section-cta-overlap-bg-5k {
  position: relative;
  padding: 100px 0;
  color: var(--color-light);
  overflow: hidden;
}
.section-cta-overlap-bg-5k .background-element {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}
.section-cta-overlap-bg-5k .background-element img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  filter: brightness(0.5) blur(2px);
}
.section-cta-overlap-bg-5k .container {
  position: relative;
  z-index: 2;
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cta-overlap-bg-5k .content-box {
  background-color: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  padding: 40px;
  border-radius: 10px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  text-align: center;
  max-width: 700px;
  margin: 0 auto;
}
.section-cta-overlap-bg-5k .box-title {
  font-size: clamp(2rem, 6vw, 3rem);
  font-family: var(--font-heading);
  font-weight: 700;
  margin-bottom: 20px;
  color: var(--color-light);
}
.section-cta-overlap-bg-5k .box-text {
  font-size: 1.1rem;
  font-family: var(--font-body);
  color: var(--color-light);
  line-height: 1.6;
  margin-bottom: 30px;
}
.section-cta-overlap-bg-5k .btn-cta-overlap {
  background-color: var(--color-primary);
  color: var(--color-light);
  padding: 12px 30px;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease;
  font-family: var(--font-body);
}
.section-cta-overlap-bg-5k .btn-cta-overlap:hover {
  background-color: var(--color-primary-dark, #0056b3);
}
@media (max-width: 768px) {
  .section-cta-overlap-bg-5k {
    padding: 60px 0;
  }
  .section-cta-overlap-bg-5k .content-box {
    padding: 30px;
  }
}
