css:
.section-cta-split-screen-form-3c {
  padding: 60px 0;
  background-color: var(--color-surface-1);
}
.section-cta-split-screen-form-3c .split-container {
  display: flex;
  max-width: 1200px;
  margin: 0 auto;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
  overflow: hidden;
}
.section-cta-split-screen-form-3c .left-panel {
  flex: 1;
  background-color: var(--color-primary);
  color: var(--color-light);
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 50px 30px;
  font-family: var(--font-body);
}
.section-cta-split-screen-form-3c .left-panel .content {
  max-width: 450px;
  text-align: center;
}
.section-cta-split-screen-form-3c .panel-title {
  font-size: clamp(2rem, 5vw, 3rem);
  font-family: var(--font-heading);
  font-weight: 700;
  margin-bottom: 15px;
}
.section-cta-split-screen-form-3c .panel-text {
  font-size: 1rem;
  line-height: 1.6;
}
.section-cta-split-screen-form-3c .right-panel {
  flex: 1;
  background-color: var(--color-light);
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 50px 30px;
}
.section-cta-split-screen-form-3c .form-wrapper {
  width: 100%;
  max-width: 400px;
  font-family: var(--font-body);
}
.section-cta-split-screen-form-3c .form-title {
  font-size: 1.8rem;
  font-family: var(--font-heading);
  font-weight: 700;
  color: var(--color-dark);
  margin-bottom: 25px;
  text-align: center;
}
.section-cta-split-screen-form-3c .form-group {
  margin-bottom: 20px;
}
.section-cta-split-screen-form-3c .form-label {
  display: block;
  margin-bottom: 8px;
  font-weight: 600;
  color: var(--color-dark);
}
.section-cta-split-screen-form-3c .form-input {
  width: 100%;
  padding: 10px;
  border: 1px solid var(--color-border);
  border-radius: 5px;
  font-size: 1rem;
  box-sizing: border-box;
}
.section-cta-split-screen-form-3c .btn-form-submit {
  width: 100%;
  padding: 12px 20px;
  background-color: var(--color-primary);
  color: var(--color-light);
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.section-cta-split-screen-form-3c .btn-form-submit:hover {
  background-color: var(--color-primary-dark, #0056b3);
}
@media (max-width: 768px) {
  .section-cta-split-screen-form-3c .split-container {
    flex-direction: column;
  }
  .section-cta-split-screen-form-3c .left-panel {
    flex-basis: auto;
  }
  .section-cta-split-screen-form-3c .right-panel {
    flex-basis: auto;
  }
}
