css:
.section-hero-modern-cta-4r {
  background-color: var(--color-surface-1);
  padding: 80px 0;
  color: var(--color-dark);
}
.section-hero-modern-cta-4r .container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 40px;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-hero-modern-cta-4r .content-wrapper {
  flex: 1;
  max-width: 550px;
}
.section-hero-modern-cta-4r .hero-title {
  font-size: clamp(2.5rem, 6vw, 4rem);
  font-family: var(--font-heading);
  font-weight: 700;
  margin-bottom: 20px;
  color: var(--color-primary);
}
.section-hero-modern-cta-4r .hero-subtitle {
  font-size: 1.2rem;
  font-family: var(--font-body);
  margin-bottom: 30px;
  color: var(--color-secondary);
  line-height: 1.6;
}
.section-hero-modern-cta-4r .cta-buttons {
  display: flex;
  gap: 15px;
}
.section-hero-modern-cta-4r .btn {
  padding: 12px 25px;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;
  font-family: var(--font-body);
}
.section-hero-modern-cta-4r .btn-primary {
  background-color: var(--color-primary);
  color: var(--color-light);
}
.section-hero-modern-cta-4r .btn-primary:hover {
  background-color: var(--color-primary-dark, #0056b3);
}
.section-hero-modern-cta-4r .btn-secondary {
  background-color: transparent;
  color: var(--color-primary);
  border: 2px solid var(--color-primary);
}
.section-hero-modern-cta-4r .btn-secondary:hover {
  background-color: var(--color-primary);
  color: var(--color-light);
}
.section-hero-modern-cta-4r .image-wrapper {
  flex: 1;
  max-width: 500px;
}
.section-hero-modern-cta-4r .image-wrapper img {
  width: 100%;
  height: auto;
  display: block;
  border-radius: 8px;
}
@media (max-width: 768px) {
  .section-hero-modern-cta-4r .container {
    flex-direction: column;
    text-align: center;
  }
  .section-hero-modern-cta-4r .content-wrapper {
    margin-bottom: 30px;
  }
  .section-hero-modern-cta-4r .cta-buttons {
    justify-content: center;
  }
}
