css:
.section-faq-card-layout-z2x {
    padding: 60px 0;
    background-color: var(--color-light);
}
.section-faq-card-layout-z2x .container {
    max-width: 1140px;
    margin: 0 auto;
    padding: 0 20px;
}
.section-faq-card-layout-z2x .faq-title {
    font-size: clamp(2rem, 5vw, 3rem);
    font-family: var(--font-heading);
    color: var(--color-dark);
    text-align: center;
    margin-bottom: 50px;
}
.section-faq-card-layout-z2x .faq-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 30px;
}
.section-faq-card-layout-z2x .faq-card {
    background-color: var(--color-surface-1);
    border-radius: 10px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.08);
    overflow: hidden;
}
.section-faq-card-layout-z2x .faq-item {
    border: none;
}
.section-faq-card-layout-z2x .faq-item > summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 25px;
    background-color: var(--color-surface-1);
    transition: background-color 0.3s ease;
    border-bottom: 1px solid var(--color-border);
}
.section-faq-card-layout-z2x details.faq-item[open] > summary {
    background-color: var(--color-surface-1);
}
.section-faq-card-layout-z2x .faq-item > summary:hover {
    background-color: var(--color-surface-1);
}
.section-faq-card-layout-z2x .faq-question {
    flex-grow: 1;
    font-family: var(--font-body);
    font-size: 1.1rem;
    color: var(--color-dark);
    font-weight: 500;
    margin-right: 15px;
}
.section-faq-card-layout-z2x .faq-icon {
    width: 20px;
    height: 20px;
    background-color: var(--color-primary);
    border-radius: 50%;
    position: relative;
    margin-left: 15px;
    flex-shrink: 0;
    transition: transform 0.3s ease;
}
.section-faq-card-layout-z2x .faq-icon::before, .section-faq-card-layout-z2x .faq-icon::after {
    content: '';
    position: absolute;
    background-color: var(--color-light);
    border-radius: 2px;
}
.section-faq-card-layout-z2x .faq-icon::before {
    width: 10px;
    height: 2px;
    top: 9px;
    left: 5px;
}
.section-faq-card-layout-z2x .faq-icon::after {
    width: 2px;
    height: 10px;
    top: 5px;
    left: 9px;
}
.section-faq-card-layout-z2x details[open] .faq-icon {
    transform: rotate(45deg);
}
.section-faq-card-layout-z2x .faq-answer {
    padding: 0 25px 25px 25px;
    color: var(--color-secondary);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.6;
    background-color: var(--color-surface-1);
    border-top: 1px solid var(--color-border);
    margin-top: -1px;
}
.section-faq-card-layout-z2x .faq-answer p {
    margin: 0;
    padding-top: 10px;
}
