css:
.section-faq-gradient-reveal-p9g {
    padding: 70px 0;
    background: linear-gradient(135deg, var(--color-primary), var(--color-secondary));
}
.section-faq-gradient-reveal-p9g .container {
    max-width: 900px;
    margin: 0 auto;
    padding: 0 20px;
}
.section-faq-gradient-reveal-p9g .accordion-container {
    background-color: var(--color-surface-1);
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 8px 30px rgba(0, 0, 0, 0.15);
}
.section-faq-gradient-reveal-p9g .faq-item {
    border-bottom: 1px solid var(--color-border);
}
.section-faq-gradient-reveal-p9g .faq-item:last-child {
    border-bottom: none;
}
.section-faq-gradient-reveal-p9g .faq-item > summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 25px 35px;
    background-color: var(--color-surface-1);
    transition: background-color 0.3s ease;
}
.section-faq-gradient-reveal-p9g details.faq-item[open] > summary {
    background-color: var(--color-surface-1);
}
.section-faq-gradient-reveal-p9g .faq-item > summary:hover {
    background-color: var(--color-surface-1);
}
.section-faq-gradient-reveal-p9g .faq-number {
    font-family: var(--font-heading);
    font-size: 1.4rem;
    font-weight: 700;
    color: var(--color-primary);
    margin-right: 20px;
    flex-shrink: 0;
}
.section-faq-gradient-reveal-p9g .faq-question {
    flex-grow: 1;
    font-family: var(--font-body);
    font-size: 1.2rem;
    color: var(--color-dark);
    font-weight: 500;
    margin-right: 20px;
}
.section-faq-gradient-reveal-p9g .faq-icon {
    width: 28px;
    height: 28px;
    background-color: var(--color-primary);
    border-radius: 50%;
    position: relative;
    margin-left: 20px;
    flex-shrink: 0;
    transition: transform 0.3s ease;
}
.section-faq-gradient-reveal-p9g .faq-icon::before, .section-faq-gradient-reveal-p9g .faq-icon::after {
    content: '';
    position: absolute;
    background-color: var(--color-light);
    border-radius: 2px;
}
.section-faq-gradient-reveal-p9g .faq-icon::before {
    width: 14px;
    height: 2px;
    top: 13px;
    left: 7px;
}
.section-faq-gradient-reveal-p9g .faq-icon::after {
    width: 2px;
    height: 14px;
    top: 7px;
    left: 13px;
}
.section-faq-gradient-reveal-p9g details[open] .faq-icon {
    transform: rotate(45deg);
}
.section-faq-gradient-reveal-p9g .faq-answer {
    padding: 0 35px 35px 35px;
    color: var(--color-secondary);
    font-family: var(--font-body);
    font-size: 1.1rem;
    line-height: 1.7;
    background-color: var(--color-surface-1);
    border-top: 1px solid var(--color-border);
    margin-top: -1px;
}
.section-faq-gradient-reveal-p9g .faq-answer p {
    margin: 0;
    padding-top: 15px;
}
