css:
.section-faq-minimalist-contrast-m3p {
    padding: 60px 0;
    background-color: var(--color-light);
}
.section-faq-minimalist-contrast-m3p .container {
    max-width: 800px;
    margin: 0 auto;
    padding: 0 20px;
}
.section-faq-minimalist-contrast-m3p .accordion-container {
    background-color: var(--color-surface-1);
    border-radius: 8px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}
.section-faq-minimalist-contrast-m3p .faq-item {
    border-bottom: 1px solid var(--color-border);
}
.section-faq-minimalist-contrast-m3p .faq-item:last-child {
    border-bottom: none;
}
.section-faq-minimalist-contrast-m3p .faq-item > summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 25px 30px;
    background-color: var(--color-surface-1);
    transition: background-color 0.3s ease;
}
.section-faq-minimalist-contrast-m3p details.faq-item[open] > summary {
    background-color: var(--color-surface-1);
}
.section-faq-minimalist-contrast-m3p .faq-item > summary:hover {
    background-color: var(--color-surface-1);
}
.section-faq-minimalist-contrast-m3p .faq-question {
    flex-grow: 1;
    font-family: var(--font-body);
    font-size: 1.2rem;
    color: var(--color-dark);
    font-weight: 500;
    margin-right: 20px;
}
.section-faq-minimalist-contrast-m3p .faq-icon {
    width: 24px;
    height: 24px;
    background-color: var(--color-primary);
    border-radius: 50%;
    position: relative;
    flex-shrink: 0;
    transition: transform 0.3s ease;
}
.section-faq-minimalist-contrast-m3p .faq-icon::before, .section-faq-minimalist-contrast-m3p .faq-icon::after {
    content: '';
    position: absolute;
    background-color: var(--color-light);
    border-radius: 2px;
}
.section-faq-minimalist-contrast-m3p .faq-icon::before {
    width: 12px;
    height: 2px;
    top: 11px;
    left: 6px;
}
.section-faq-minimalist-contrast-m3p .faq-icon::after {
    width: 2px;
    height: 12px;
    top: 6px;
    left: 11px;
}
.section-faq-minimalist-contrast-m3p details[open] .faq-icon {
    transform: rotate(45deg);
}
.section-faq-minimalist-contrast-m3p .faq-answer {
    padding: 0 30px 30px 30px;
    color: var(--color-secondary);
    font-family: var(--font-body);
    font-size: 1.1rem;
    line-height: 1.7;
    background-color: var(--color-surface-1);
    border-top: 1px solid var(--color-border);
    margin-top: -1px;
}
.section-faq-minimalist-contrast-m3p .faq-answer p {
    margin: 0;
    padding-top: 10px;
}
