css:
.section-faq-modern-accordion-7k1 {
    padding: 60px 0;
    background-color: var(--color-surface-1);
}
.section-faq-modern-accordion-7k1 .container {
    max-width: 960px;
    margin: 0 auto;
    padding: 0 20px;
}
.section-faq-modern-accordion-7k1 .faq-title {
    font-size: clamp(2rem, 5vw, 3rem);
    font-family: var(--font-heading);
    color: var(--color-dark);
    text-align: center;
    margin-bottom: 15px;
}
.section-faq-modern-accordion-7k1 .faq-subtitle {
    font-size: 1.1rem;
    font-family: var(--font-body);
    color: var(--color-secondary);
    text-align: center;
    margin-bottom: 40px;
}
.section-faq-modern-accordion-7k1 .accordion-container {
    border: 1px solid var(--color-border);
    border-radius: 8px;
    overflow: hidden;
}
.section-faq-modern-accordion-7k1 .faq-item {
    border-bottom: 1px solid var(--color-border);
}
.section-faq-modern-accordion-7k1 .faq-item:last-child {
    border-bottom: none;
}
.section-faq-modern-accordion-7k1 .faq-item > summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 20px;
    background-color: var(--color-surface-1);
    transition: background-color 0.3s ease;
}
.section-faq-modern-accordion-7k1 details.faq-item[open] > summary {
    background-color: var(--color-surface-1);
}
.section-faq-modern-accordion-7k1 .faq-item > summary:hover {
    background-color: var(--color-surface-1);
}
.section-faq-modern-accordion-7k1 .faq-number {
    font-family: var(--font-heading);
    font-size: 1.2rem;
    font-weight: bold;
    color: var(--color-secondary);
    margin-right: 15px;
    flex-shrink: 0;
}
.section-faq-modern-accordion-7k1 .faq-question {
    flex-grow: 1;
    font-family: var(--font-body);
    font-size: 1.1rem;
    color: var(--color-dark);
    font-weight: 500;
}
.section-faq-modern-accordion-7k1 .faq-icon {
    width: 20px;
    height: 20px;
    background-color: var(--color-primary);
    border-radius: 50%;
    position: relative;
    margin-left: 15px;
    flex-shrink: 0;
    transition: transform 0.3s ease;
}
.section-faq-modern-accordion-7k1 .faq-icon::before, .section-faq-modern-accordion-7k1 .faq-icon::after {
    content: '';
    position: absolute;
    background-color: var(--color-light);
    border-radius: 2px;
    transition: opacity 0.3s ease;
}
.section-faq-modern-accordion-7k1 .faq-icon::before {
    width: 10px;
    height: 2px;
    top: 9px;
    left: 5px;
}
.section-faq-modern-accordion-7k1 .faq-icon::after {
    width: 2px;
    height: 10px;
    top: 5px;
    left: 9px;
}
.section-faq-modern-accordion-7k1 details[open] .faq-icon {
    transform: rotate(45deg);
}
.section-faq-modern-accordion-7k1 .faq-answer {
    padding: 0 20px 20px 20px;
    background-color: var(--color-surface-1);
    color: var(--color-secondary);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.6;
    border-top: 1px solid var(--color-border);
    margin-top: -1px;
}
.section-faq-modern-accordion-7k1 .faq-answer p {
    margin: 0;
    padding-top: 10px;
}
