css:
.section-faq-segmented-control-a4b {
    padding: 60px 0;
    background-color: var(--color-surface-1);
}
.section-faq-segmented-control-a4b .container {
    max-width: 960px;
    margin: 0 auto;
    padding: 0 20px;
}
.section-faq-segmented-control-a4b .faq-tabs {
    display: flex;
    justify-content: center;
    margin-bottom: 40px;
    background-color: var(--color-light);
    border-radius: 8px;
    overflow: hidden;
    border: 1px solid var(--color-border);
}
.section-faq-segmented-control-a4b .faq-tab {
    padding: 15px 25px;
    font-family: var(--font-body);
    font-size: 1rem;
    font-weight: 500;
    color: var(--color-secondary);
    background-color: transparent;
    border: none;
    cursor: pointer;
    transition: all 0.3s ease;
    flex-grow: 1;
    max-width: 200px;
}
.section-faq-segmented-control-a4b .faq-tab.active {
    background-color: var(--color-primary);
    color: var(--color-light);
}
.section-faq-segmented-control-a4b .accordion-container {
    display: none;
    background-color: var(--color-surface-1);
    border-radius: 8px;
    overflow: hidden;
    border: 1px solid var(--color-border);
}
.section-faq-segmented-control-a4b .accordion-container.active {
    display: block;
}
.section-faq-segmented-control-a4b .faq-item {
    border-bottom: 1px solid var(--color-border);
}
.section-faq-segmented-control-a4b .faq-item:last-child {
    border-bottom: none;
}
.section-faq-segmented-control-a4b .faq-item > summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 20px;
    background-color: var(--color-surface-1);
    transition: background-color 0.3s ease;
}
.section-faq-segmented-control-a4b details.faq-item[open] > summary {
    background-color: var(--color-surface-1);
}
.section-faq-segmented-control-a4b .faq-item > summary:hover {
    background-color: var(--color-surface-1);
}
.section-faq-segmented-control-a4b .faq-question {
    flex-grow: 1;
    font-family: var(--font-body);
    font-size: 1.1rem;
    color: var(--color-dark);
    font-weight: 500;
    margin-right: 15px;
}
.section-faq-segmented-control-a4b .faq-icon {
    width: 20px;
    height: 20px;
    background-color: var(--color-primary);
    border-radius: 50%;
    position: relative;
    margin-left: 15px;
    flex-shrink: 0;
    transition: transform 0.3s ease;
}
.section-faq-segmented-control-a4b .faq-icon::before, .section-faq-segmented-control-a4b .faq-icon::after {
    content: '';
    position: absolute;
    background-color: var(--color-light);
    border-radius: 2px;
}
.section-faq-segmented-control-a4b .faq-icon::before {
    width: 10px;
    height: 2px;
    top: 9px;
    left: 5px;
}
.section-faq-segmented-control-a4b .faq-icon::after {
    width: 2px;
    height: 10px;
    top: 5px;
    left: 9px;
}
.section-faq-segmented-control-a4b details[open] .faq-icon {
    transform: rotate(45deg);
}
.section-faq-segmented-control-a4b .faq-answer {
    padding: 0 20px 20px 20px;
    color: var(--color-secondary);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.6;
    background-color: var(--color-surface-1);
    border-top: 1px solid var(--color-border);
    margin-top: -1px;
}
.section-faq-segmented-control-a4b .faq-answer p {
    margin: 0;
    padding-top: 10px;
}
