css:
.section-cta-card-centered {
  background-color: #D6D6D6;
  padding: 40px 20px 60px 20px;
  font-family: 'Manrope', sans-serif;
  color: #1a1a1a;
}

.section-cta-card-centered .container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-cta-card-centered .cta-card {
  background-color: #CDCDCD;
  border-radius: 32px;
  padding: 80px 20px;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.section-cta-card-centered .pre-title {
  font-size: 1.2rem;
  margin-bottom: 10px;
  opacity: 0.7;
}

.section-cta-card-centered .cta-title {
  font-size: 4rem;
  line-height: 1.1;
  font-weight: 400;
  margin-bottom: 20px;
}

.section-cta-card-centered .cta-link {
  color: #C27546;
  font-size: 1.5rem;
  text-decoration: none;
  margin-top: 10px;
  transition: opacity 0.3s;
}

.section-cta-card-centered .cta-link:hover {
  opacity: 0.8;
}
