css:
.section-feature-breakfast-card-v1 {

  --color-bg: #f5f5f5;
  --color-card-bg: #ffffff;
  --color-text: #666666;
  --color-title: #222222;
  padding: 80px 20px;
  background-color: var(--color-bg);
  font-family: 'Montserrat', sans-serif;
}

.section-feature-breakfast-card-v1 .feature-container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-feature-breakfast-card-v1 .feature-wrapper {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  position: relative;
  min-height: 500px;
}

.section-feature-breakfast-card-v1 .feature-image {
  width: 70%;
  position: relative;
  z-index: 1;
}

.section-feature-breakfast-card-v1 .feature-image img {
  width: 100%;
  height: 500px;
  object-fit: cover;
  box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}

.section-feature-breakfast-card-v1 .deco-circle {
  position: absolute;
  top: -40px;
  right: -40px;
  width: 150px;
  height: 150px;
  background-color: var(--color-primary);
  border-radius: 50%;
  z-index: -1;
}

.section-feature-breakfast-card-v1 .feature-card {
  width: 40%;
  background: var(--color-card-bg);
  padding: 50px;
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  z-index: 2;
  box-shadow: 0 15px 40px rgba(0,0,0,0.05);
}

.section-feature-breakfast-card-v1 .card-title {
  font-size: 2rem;
  color: var(--color-title);
  margin-top: 0;
  margin-bottom: 20px;
  font-weight: 700;
}

.section-feature-breakfast-card-v1 .card-text {
  color: var(--color-text);
  line-height: 1.7;
  margin-bottom: 30px;
}

.section-feature-breakfast-card-v1 .btn-learn {
  display: inline-block;
  background-color: var(--color-primary);
  color: #fff;
  padding: 12px 30px;
  text-decoration: none;
  border-radius: 50px;
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.85rem;
  transition: background-color 0.3s;
}

.section-feature-breakfast-card-v1 .btn-learn:hover {
  background-color: #c01c22;
}

@media (max-width: 992px) {
  .section-feature-breakfast-card-v1 .feature-wrapper {
    flex-direction: column-reverse;
    align-items: stretch;
  }
  .section-feature-breakfast-card-v1 .feature-image, 
  .section-feature-breakfast-card-v1 .feature-card {
    width: 100%;
    position: relative;
    left: auto;
    top: auto;
    transform: none;
  }
  .section-feature-breakfast-card-v1 .feature-card {
    margin-bottom: -50px;
    margin-left: 20px;
    width: calc(100% - 40px);
    z-index: 5;
  }
  .section-feature-breakfast-card-v1 .deco-circle {
    right: 10px;
    top: 10px;
    width: 80px;
    height: 80px;
  }
}
