css:
.section-feature-wide-v2 {
  background-color: #D6D6D6;
  padding: 40px 20px;
  font-family: 'Manrope', sans-serif;
  color: #1a1a1a;
}

.section-feature-wide-v2 .container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-feature-wide-v2 .image-container {
  width: 100%;
  height: 400px;
  border-radius: 32px;
  overflow: hidden;
  margin-bottom: 30px;
}

.section-feature-wide-v2 .image-container img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section-feature-wide-v2 .content-row {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

@media (min-width: 768px) {
  .section-feature-wide-v2 .content-row {
    flex-direction: row;
    justify-content: space-between;
    align-items: flex-start;
  }
}

.section-feature-wide-v2 .col-left {
  flex: 1;
  max-width: 500px;
}

.section-feature-wide-v2 .col-right {
  flex: 1;
  max-width: 400px;
  text-align: right;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
}

.section-feature-wide-v2 .title {
  font-size: 2.5rem;
  font-weight: 800;
  margin-bottom: 10px;
}

.section-feature-wide-v2 .subtitle {
  font-size: 1.1rem;
  opacity: 0.8;
}

.section-feature-wide-v2 .desc {
  font-size: 0.9rem;
  line-height: 1.6;
  opacity: 0.7;
  margin-bottom: 20px;
  text-align: left;
}

@media (min-width: 768px) {
   .section-feature-wide-v2 .desc {
      text-align: right;
   }
}

.section-feature-wide-v2 .meta {
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  opacity: 0.6;
  display: flex;
  flex-direction: column;
}
