css:
.section-footer-newsletter-v1 {

  --color-bg: #111111;
  --color-text: #ffffff;
  background-color: var(--color-bg);
  color: var(--color-text);
  font-family: 'Montserrat', sans-serif;
  padding: 0;
  overflow: hidden;
}

.section-footer-newsletter-v1 .footer-container {
  display: flex;
  flex-wrap: wrap;
  min-height: 400px;
}

.section-footer-newsletter-v1 .footer-visual {
  flex: 1;
  min-width: 300px;
  position: relative;
}

.section-footer-newsletter-v1 .footer-visual img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section-footer-newsletter-v1 .visual-border {
  position: absolute;
  bottom: 0;
  right: 0;
  width: 50px;
  height: 100%;
  background: var(--color-primary);
}

.section-footer-newsletter-v1 .footer-content {
  flex: 1;
  min-width: 300px;
  padding: 60px;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.section-footer-newsletter-v1 .subscribe-block {
  margin-bottom: 50px;
}

.section-footer-newsletter-v1 .subscribe-block h3 {
  font-size: 2rem;
  margin-bottom: 10px;
  text-transform: uppercase;
}

.section-footer-newsletter-v1 .sub-form {
  display: flex;
  margin-top: 20px;
  border-bottom: 1px solid #555;
}

.section-footer-newsletter-v1 .sub-form input {
  flex: 1;
  background: transparent;
  border: none;
  color: #fff;
  padding: 10px 0;
  outline: none;
}

.section-footer-newsletter-v1 .sub-form button {
  background: var(--color-primary);
  color: #fff;
  border: none;
  padding: 10px 20px;
  text-transform: uppercase;
  font-weight: 700;
  cursor: pointer;
}

.section-footer-newsletter-v1 .info-block {
  display: flex;
  justify-content: space-between;
}

.section-footer-newsletter-v1 .info-col h4 {
  text-transform: uppercase;
  letter-spacing: 2px;
  margin-bottom: 15px;
}

.section-footer-newsletter-v1 .info-col p {
  font-size: 0.9rem;
  line-height: 1.5;
  color: #aaa;
}

.section-footer-newsletter-v1 .social-icons span {
  display: inline-block;
  margin-right: 10px;
  font-weight: bold;
  background: #fff;
  color: #000;
  width: 25px;
  height: 25px;
  text-align: center;
  line-height: 25px;
  border-radius: 50%;
  font-size: 0.8rem;
  cursor: pointer;
}

@media (max-width: 768px) {
  .section-footer-newsletter-v1 .footer-visual {
    height: 200px;
  }
  .section-footer-newsletter-v1 .info-block {
    flex-direction: column;
    gap: 30px;
  }
  .section-footer-newsletter-v1 .visual-border {
    display: none;
  }
}
