css:
.section-hero-dining-v1 {


  --color-text: #555555;
  --color-bg: #ffffff;
  padding: 80px 20px;
  background-color: var(--color-bg);
  font-family: 'Montserrat', sans-serif;
  overflow: hidden;
}

.section-hero-dining-v1 .hero-container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
  gap: 40px;
}

.section-hero-dining-v1 .hero-content {
  flex: 1;
  min-width: 300px;
}

.section-hero-dining-v1 .hero-subtitle {
  color: var(--color-primary);
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 0;
  line-height: 1.2;
}

.section-hero-dining-v1 .hero-title {
  color: var(--color-dark);
  font-size: 3.5rem;
  font-weight: 700;
  margin-top: 0;
  margin-bottom: 20px;
  line-height: 1.1;
}

.section-hero-dining-v1 .hero-desc {
  color: var(--color-text);
  margin-bottom: 30px;
  line-height: 1.6;
  max-width: 500px;
}

.section-hero-dining-v1 .btn-primary {
  display: inline-block;
  background-color: var(--color-primary);
  color: #fff;
  padding: 12px 30px;
  text-decoration: none;
  border-radius: 50px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 1px;
  transition: opacity 0.3s;
}

.section-hero-dining-v1 .btn-primary:hover {
  opacity: 0.9;
}

.section-hero-dining-v1 .hero-visual {
  flex: 1;
  min-width: 300px;
  position: relative;
  display: flex;
  justify-content: center;
}

.section-hero-dining-v1 .blob-shape {
  position: absolute;
  top: 10%;
  right: -20px;
  width: 100%;
  height: 100%;
  background-color: var(--color-primary);
  border-radius: 46% 54% 39% 61% / 45% 36% 64% 55%;
  z-index: 0;
  transform: scale(1.1) rotate(10deg);
}

.section-hero-dining-v1 .hero-img {
  position: relative;
  z-index: 1;
  width: 100%;
  max-width: 450px;
  border-radius: 0 0 200px 200px;
  mask-image: linear-gradient(to bottom, black 80%, transparent 100%);
  -webkit-mask-image: linear-gradient(to bottom, black 80%, transparent 100%);
}

@media (max-width: 768px) {
  .section-hero-dining-v1 .hero-container {
    flex-direction: column;
    text-align: center;
  }
  .section-hero-dining-v1 .hero-desc {
    margin: 0 auto 30px;
  }
  .section-hero-dining-v1 .blob-shape {
    width: 80%;
    height: 80%;
    top: 10%;
    right: 10%;
  }
}
