css:
@import url('https://fonts.googleapis.com/css2?family=Manrope:wght@400;600;800&display=swap');

.section-hero-split-card-v1 {
  background-color: #D6D6D6;
  padding: 20px;
  font-family: 'Manrope', sans-serif;
  color: #1a1a1a;
}

.section-hero-split-card-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-hero-split-card-v1 .grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 20px;
}

@media (min-width: 768px) {
  .section-hero-split-card-v1 .grid {
    grid-template-columns: 1fr 1fr;
    height: 600px;
  }
}

.section-hero-split-card-v1 .card {
  border-radius: 32px;
  overflow: hidden;
  height: 100%;
}

.section-hero-split-card-v1 .content-card {
  background-color: #CDCDCD;
  padding: 40px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-height: 400px;
}

.section-hero-split-card-v1 .image-card {
  background-color: #000;
  position: relative;
}

.section-hero-split-card-v1 .image-card img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.section-hero-split-card-v1 .title {
  font-size: 3rem;
  font-weight: 800;
  line-height: 1.1;
  margin-bottom: 24px;
}

.section-hero-split-card-v1 .subtitle {
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 24px;
  opacity: 0.8;
}

.section-hero-split-card-v1 .description {
  font-size: 1rem;
  line-height: 1.6;
  opacity: 0.7;
  max-width: 400px;
}

.section-hero-split-card-v1 .content-bottom {
  margin-top: auto;
  display: flex;
  flex-direction: column;
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  opacity: 0.6;
}
