css:
.section-menu-burger-grid-v1 {

  --color-bg: #000000;
  --color-text: #ffffff;
  padding: 80px 20px;
  background-color: var(--color-bg);
  font-family: 'Montserrat', sans-serif;
  color: var(--color-text);
}

.section-menu-burger-grid-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-menu-burger-grid-v1 .section-title {
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 10px;
  font-weight: 700;
}

.section-menu-burger-grid-v1 .section-subtitle {
  text-align: center;
  margin-bottom: 50px;
  color: #cccccc;
  font-size: 0.9rem;
}

.section-menu-burger-grid-v1 .menu-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 30px;
}

.section-menu-burger-grid-v1 .menu-card {
  display: flex;
  align-items: center;
  justify-content: space-between;
  border-bottom: 1px solid #333;
  padding-bottom: 20px;
}

.section-menu-burger-grid-v1 .card-content {
  flex: 1;
}

.section-menu-burger-grid-v1 .item-name {
  font-size: 1.5rem;
  font-weight: 700;
  margin: 0 0 10px 0;
}

.section-menu-burger-grid-v1 .item-price {
  color: var(--color-primary);
  font-weight: 600;
  margin-bottom: 15px;
}

.section-menu-burger-grid-v1 .btn-add {
  display: inline-block;
  background-color: var(--color-primary);
  color: #fff;
  padding: 8px 20px;
  text-decoration: none;
  font-size: 0.75rem;
  border-radius: 20px;
  text-transform: uppercase;
  transition: background-color 0.3s;
}

.section-menu-burger-grid-v1 .btn-add:hover {
  background-color: #c01c22;
}

.section-menu-burger-grid-v1 .card-img {
  width: 150px;
  height: 100px;
  overflow: hidden;
  border-radius: 10px;
  margin-left: 20px;
}

.section-menu-burger-grid-v1 .card-img img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

@media (max-width: 768px) {
  .section-menu-burger-grid-v1 .menu-grid {
    grid-template-columns: 1fr;
  }
}
