css:
.section-services-dark-grid-v1 {

  --color-bg: #1a1a1a;
  --color-card-bg: #222222;
  --color-text: #aaaaaa;
  --color-title: #ffffff;
  padding: 80px 20px;
  background-color: var(--color-bg);
  font-family: 'Montserrat', sans-serif;
}

.section-services-dark-grid-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-services-dark-grid-v1 .service-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
}

.section-services-dark-grid-v1 .service-card {
  background-color: var(--color-card-bg);
  padding: 40px;
  position: relative;
  transition: transform 0.3s;
}

.section-services-dark-grid-v1 .service-card:hover {
  transform: translateY(-5px);
}

.section-services-dark-grid-v1 .card-number {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 50px;
  height: 50px;
  background-color: var(--color-primary);
  color: #fff;
  border-radius: 50%;
  font-weight: 700;
  font-size: 1.2rem;
  margin-bottom: 20px;
}

.section-services-dark-grid-v1 .card-title {
  color: var(--color-title);
  font-size: 1.25rem;
  margin-bottom: 15px;
  text-transform: capitalize;
}

.section-services-dark-grid-v1 .card-text {
  color: var(--color-text);
  font-size: 0.9rem;
  line-height: 1.6;
  margin-bottom: 25px;
  font-style: italic;
}

.section-services-dark-grid-v1 .link-learn {
  color: #fff;
  text-decoration: underline;
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  opacity: 0.8;
  transition: opacity 0.3s;
}

.section-services-dark-grid-v1 .link-learn:hover {
  opacity: 1;
}
